import React, { useState, useEffect } from "react";
import LazyLoad from "react-lazyload";
import './LazyImageFolder.css'

function LazyImage({ src }) {

  return (
    <LazyLoad height={200} offset={100} once={true}>
        <img src={src} alt="Lazy-loaded image"/>
    </LazyLoad>
  );
};

function LazyImageFolder({ dir, onCloseLazyImage }) {
  const [images, setImages] = useState([]);
  console.log("before")
  useEffect(() => {
    fetch(`/api/images?dir=${encodeURIComponent(dir)}`)
      .then((response) => response.json())
      .then((data) => setImages(data.imageByteList))
      .catch((error) => console.error(error));
  }, [dir]);

  console.log("in load image")
  function renderImages(images) {
    return images.map((image_byte, index) => (
      <div className="lazy-image">
        <LazyImage key={index} src={image_byte} />
      </div>

    ));
  }

  return (
    <div className="lazy-component">
      <button onClick={() => onCloseLazyImage()}>Go Back</button>
      <div>
        {renderImages(images)}
      </div>;
    </div>
  )

}

export default LazyImageFolder;
