import React, { useState, useEffect } from "react";
import LazyLoad from "react-lazyload";
import './LazyImageFolder.css'

function LazyImage({ data }) {
  return (
    <div className="lazy-image">
      <LazyLoad height={200}>
        <img src={data} />
      </LazyLoad>
    </div>

  );
}

function LazyImageFolder({ dir }) {
  const [images, setImages] = useState([]);

  useEffect(() => {
    fetch(`/api/images?dir=${encodeURIComponent(dir)}`)
      .then((response) => response.json())
      .then((data) => setImages(data.imageByteList))
      .catch((error) => console.error(error));
  }, [dir]);

  console.log("in load image")
  function renderImages(images) {
    return images.map((image_byte) => (
      <LazyImage data={image_byte} />
    ));
  }

  return <div>{renderImages(images)}</div>;

}

export default LazyImageFolder;
