import React from "react";
import { useState, useStateValue } from "./StateProvider";

const SideBar = () => {
    const [state, dispatch] = useStateValue("");

    const handleCategorySelect = (category) => {
        setSelectedCategory(category);
    }

    return (
        <div className="category-menu">
            <div onClick={() => handleCategorySelect("category1")}>Category 1</div>
            <div onClick={() => handleCategorySelect("category2")}>Category 2</div>
            <div onClick={() => handleCategorySelect("category3")}>Category 3</div>
        </div>
    )

}

export default SideBar;
