import React, { useState }from "react";
import { useStateValue } from "./StateProvider";

function Tag({ tag, filter }) {
    const [state, dispatch] = useStateValue();
    const [clicked, setClicked] = useState(false);

    const onTagClick = (tag, filter) => {
        //console.log(tag, filter);

        setClicked(!clicked);

        if (clicked) {
            dispatch({
                type: 'addTag',
                item: {
                    filter: filter,
                    tag: tag,
                }
            })
        }

    } 

    const clickedColor = {
        color: "red"
    }
    const unclickedColor = {
        color : '#aaa'
    }
    return (     
        <li  style={clicked? clickedColor : unclickedColor} onClick={(e) => onTagClick(tag, filter)} className="tag clickable">
            {tag}
        </li>
    )
}

export default Tag;
