import React from "react";
import { useStateValue } from './StateProvider';
import Helpers from "./Helpers";


function SearchBar () {
    const [tagList] = useStateValue();

    const handleNewTag = (tags) => {
      if (this.props.onNewTag) this.props.onNewTag(tags);
      if (this.props.onTagChange) this.props.onTagChange(tags);
    }

    const handleInputChange = ({target: { value: inputValue }}) => {
      inputValue = inputValue === ',' ? '' : inputValue;
      this.setState({inputValue});
    }

    const handleKeyDown = (e) => {
      let { key, target: {value} } = e;
      let { tags } = this.state;
      switch (key) {
        case 'Tab':
          if (value) e.preventDefault();
        case 'Enter':
        case ',':
          value = value.trim();
          if (value && notDuplicate(tags, value)) {
            addTag(value);
          } else {
            this.setState({inputValue: ''})
          }
          break;
        case 'Backspace':
          if (!value) {
            handleTagDelete(tags.length - 1);
          }
          break;
      }
    }

    const handleTagDelete = (index, e) => {
      deleteTag(index, () => {
        this.props.onTagChange(this.state.tags);
      });
    }

    const deleteTag = (index, callback) => {
      let tags = this.state.tags.slice();

      tags.splice(index, 1);
      this.setState({ tags }, () => {
        if (callback) callback();
      });
    }

    const notDuplicate = (tags, newTag) => {
      return (!tags.includes(newTag) || this.props.allowDuplicates);
    }

    const addTag = (tag) => {
      if (this.notDuplicate(this.state.tags, tag)) {
        this.setState({tags: [...this.state.tags, tag], inputValue: ''}, () => {
          handleNewTag(this.state.tags);
        });
      }
    }

    const updateControlledTags = (tags) => {
      if (tags && !Helpers.hasDuplicates(tags)) {
        this.setState({ tags }, () => {
          // this.props.onTagChange(tags);
        });
      }
    }

    const componentWillReceiveProps = (nextProps) => {
      updateControlledTags(nextProps.tags);
    }

      return (
        <span className="tagInputWrapper">
          <TagsList 
            tags={this.state.tags} 
            onTagDelete={this.handleTagDelete} 
            hashtag={this.props.hashtag}
          />
          <input 
            name="tagInput" 
            className="tagInput" 
            placeholder="enter a tag..." 
            value={inputValue} 
            onChange={this.handleInputChange}
            onKeyDown={this.handleKeyDown}
          />
        </span>
      );
}

const TagsList = ({tags, onTagDelete, hashtag}) => {
    let list = tags.map((tag, index) => (
      <DeletableTag 
        name={tag} 
        onDelete={onTagDelete} 
        index={index} 
        hashtag={hashtag} />
    ));
    return (
      <ul name="tagsList" className="tagsList">
        {list}
      </ul>
    )
  }

  const DeletableTag = ({name, index, onDelete, hashtag}) => {
    return (
      <li>
        {hashtag && (
          <span style={{color: 'rgb(87, 7, 7)', fontWeight: 'bold'}}># </span>
        )}
        {name} 
        <a href="#" onClick={e => onDelete(index, e)}>x</a>
      </li>
    );
  }

export default SearchBar;
