    setButton(false)
  }

  const presentLayer = () => {
    if (frame[0]) {
      console.log('in frame')
      return(
        <WebFrame />
      )

    } else {
      return (
        <div className='searchlayer'>
          {!button && <Route path='/'><SearchBar /></Route> }
          {!button && <WebBlocks filter={tagList}/>}
          {!button && <button className='image_load' onClick={(e) => loadImage()}>Check out this ads page</button>}
          {button && <Route path='/ads-banner'><LazyImageFolder dir={"../src/ads_img"} onCloseLazyImage={onCloseLazyImage}/></Route>}
