import './App.css';
import React, { useEffect } from 'react';
import { useStateValue } from './StateProvider';
import { render } from '@testing-library/react';
import { toHaveStyle } from '@testing-library/jest-dom/dist/matchers';

export default class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      filter: null,
      detail: null,
      url: null,
      openurl: false,
    };
  }



  handleRenderLayer = () => {
    switch(this.state.filter) {
      case 'book':
        return (
            <div className="category-menu">
                  <div onClick={() => this.setState({filter: null})}>Go Back</div>
                  <div onClick={() => this.setState({filter: 'url_list', detail: 'bookid'})}>全球中文网址速查手册（2000）</div>
                  <div>2002因特网黄页网址簿</div>
                  <div>全球中文网址速查手册（1999）</div>
                  <div>2001因特网黄页网址簿</div>
                  <div>Internet 全中文网址簿</div>
                  <div>Harley Hahn's Internet and Web Yellow Pages, Millennium Edition</div>
                  <div>Que's Official Internet Yellow Pages 2001 Edition</div>
            </div>
        ); 
      case 'url_list':
        return (
          <div className= "category-menu">
            <ul>
              <li onClick={() => this.setState({filter: 'selected', openurl: true, url: 'https://web.archive.org/web/19990508051230id_/http://www.tigtail.org/tvm/x1/baroque-spanish.html'})}>Tigertail Virtual Museum</li>
            </ul>
          </div>
        )
      case 'selected':
        return (
          <div className="category-menu">
            <p>
              Title: <br></br>
              Language: <br></br>
              Book Included: <br></br>
            </p>

            <div onClick={() => this.setState({filter: 'selected', openurl: true, url: 'https://web.archive.org/web/19990508051230id_/http://www.tigtail.org/tvm/x1/baroque-spanish.html'})}>
              Go Back
            </div>
          </div>
        ) 

      default:
        return (
            <div className="category-menu">
                  <h3>Please select a filter group</h3>
                  <div onClick={() => this.setState({filter: "book"})}>Book</div>
                  <div onClick={() => this.setState({filter: "category"})}>Category</div>
                  <div onClick={() => this.setState({filter: "date"})}>Date</div>
            </div>

        );
    }
  }

  handlePresentLayer = () => {
    switch(this.state.openurl) {
      case true:
        return (
          <div>
            <iframe src={this.state.url} allow='picture-in-picture full' width={1270} height={1000}></iframe>
          </div>
        )
      default:
        return (
          <div>
            <h3>This is a short introduction</h3>
          </div>
        )
    }
  }
  render() {
    return (
      <div className="app">

        <div>{this.handleRenderLayer()}</div>
        <div className="frame">
          {this.handlePresentLayer()}
        </div>
      </div>
    );
  }




};
