import './ListItem.css';
import { useStateValue } from "./StateProvider";
import Tag from './Tag.js;'


function ListItem({ url, book_name, year_published, category, onClickUrl }) {
    const [state, dispatch] = useStateValue();

    // const tags = book_name.concat(year_published, category);

    const onBookTagClick = () => {
        console.log("I did it");
    }

    const onYearTagClick = () => {
        console.log("I did it");
    }

    const onCategoryTagClick = () => {
        console.log("I did it");
    }

    const renderedBookTags = book_name.map((tag, index) => {
        <Tag 
        filter='book'
        tag={tag}
        />
    });
    const renderedYearTags = year_published.map((tag, index) => {
        <Tag 
        filter='year'
        tag={tag}
        />
    });

    const renderedCategoryTags = category.map((tag, index) => {
        <Tag 
        filter='category'
        tag={tag}
        />
    });

    return (
    <li style={{padding: 10, listStyle: 'none', background: '#efefef', borderRadius: '10px', boxShadow: '1px 1px 5px #aaa'}}>
        <h2 onClick={(e) => onClickUrl(url, e)} style={{color: 'black'}} >{url}</h2>
        <ul className='tagsList' style={{padding: 0}}>{renderedBookTags}{renderedYearTags}{renderedCategoryTags}</ul>
    </li>
    );
}

export default ListItem;
