import './App.css';
import React, { useEffect } from 'react';
import { useStateValue } from './StateProvider';
import { render } from '@testing-library/react';

export default class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      category: null,
      url: null,
    };
  }



  handleRenderLayer = () => {
    switch(this.state.category) {
      case 'book':
        return (
            <div className="category-menu">
                  <div onClick={() => this.setState({category: null})}>Go Back</div>
                  <div>全球中文网址速查手册（2000）</div>
                  <div>2002因特网黄页网址簿</div>
                  <div>全球中文网址速查手册（1999）</div>
                  <div>2001因特网黄页网址簿</div>
                  <div>Internet 全中文网址簿</div>
                  <div>Harley Hahn's Internet and Web Yellow Pages, Millennium Edition</div>
                  <div>Que's Official Internet Yellow Pages 2001 Edition</div>
            </div>
        ); 
      default:
        return (
            <div className="category-menu">
                  <h3>Please select a filter group</h3>
                  <div onClick={() => this.setState({category: "book"})}>Book</div>
                  <div onClick={() => this.setState({category: "category"})}>Category</div>
                  <div onClick={() => this.setState({category: "date"})}>Date</div>
            </div>

        );
    }
  }

  handlePresentLayer = () => {

  }
  render() {
    return (
      <div className="app">

        <div>{this.handleRenderLayer()}</div>
        <div className="frame">
          <iframe src='https://web.archive.org/web/19990508051230id_/http://www.tigtail.org/tvm/x1/baroque-spanish.html' allow='picture-in-picture full' width={1270} height={1000}></iframe>
        </div>
      </div>
    );
  }




};
