import './App.css';
import React, { useEffect, useState } from 'react';
import { useStateValue } from './StateProvider';
import SearchBar from './SearchBar';
import WebBlocks from './WebBlocks';
import axios from 'axios';
import WebFrame from './WebFrame';
import Homebar from './Homebar';

function App () {
  const [showContent, setShowContent] = useState(false);
  const [{tagList, frame, searchTerm}, dispatch] = useStateValue(); 

  useEffect(() => {
    console.log('fetching fist page...')
    axios.get('/firstpage')
        .then(res => {
          dispatch({
            type: 'addElement',
            item: res.data,
          })
        })
        .catch(err => console.log(err));
  }, []);

  useEffect(() => {
    console.log('searching term from json...')
    axios.get('/search', {
      params: {
        search: searchTerm
      }}
    )
      .then(res => {
          dispatch({
            type: 'filteredId',
            item: res.data,
          })
      })
        .catch(err => console.log(err));
  }, [searchTerm]);

  const DisclaimerAlert = () => {
    const [showAlert, setShowAlert] = useState(true);

    const handleAccept = () => {
      setShowAlert(false)
      setShowContent(true);
      // You can add additional logic here to handle user's acceptance of the disclaimer
    };

    return (
      showAlert && (
        <div className="disclaimer-alert">
          <h1>Disclaimer</h1>
          <p>This is a disclaimer message. Please read and accept to continue.</p>
          <button onClick={handleAccept}>Accept</button>
        </div>
      )
    );
  };

  const presentLayer = () => {
    if (frame[0]) {
      console.log('in frame')
      return(
        <WebFrame />
      )

    } else {
      return (
        <div className='searchlayer'>
          <SearchBar />
          <WebBlocks filter={tagList} />
          {/* {!button && <button className='image_load' onClick={(e) => loadImage()}>Check out this ads page</button>} */}
          {/* {button && <LazyImageFolder dir={"../src/ads_img"} onCloseLazyImage={onCloseLazyImage}/>} */}
        </div>
      )
    }
  }

  return (
    <div className="app">
      <Homebar />
      {<DisclaimerAlert />}
      {showContent && presentLayer()}
      {/* <Stream /> */}
    </div>
  )


};

export default App;
