import './App.css';
import React from 'react';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedCategory: "category1"
    };
  }

  handleCategorySelect(category) {
    this.setState({ selectedCategory: category });
  }

  render() {
    return (
      <div>
        <Sidebar onCategorySelect={this.handleCategorySelect.bind(this)} />
        <p category={this.state.selectedCategory} />
      </div>
    );
  }
}

function Sidebar(props) {
  return (
    <div>
      <button onClick={() => props.onCategorySelect("category1")}>Category 1</button>
      <button onClick={() => props.onCategorySelect("category2")}>Category 2</button>
      <button onClick={() => props.onCategorySelect("category3")}>Category 3</button>
    </div>
  );
}


export default App;
