import React, { useState, useEffect } from "react";
import LazyLoad from "react-lazyload";

function LazyImage({ src, alt }) {
  return (
    <LazyLoad height={200}>
      <img src='/public/ads_img/60e9ce6ffcea6524f06818b2-20011202144700-32.gif' alt={alt} />
    </LazyLoad>
  );
}

function LazyImageFolder({ dir }) {
  const [images, setImages] = useState([]);

  useEffect(() => {
    fetch(`/api/images?dir=${encodeURIComponent(dir)}`)
      .then((response) => response.json())
      .then((data) => setImages(data.imagePaths))
      .catch((error) => console.error(error));
  }, [dir]);

  console.log("in load image")
  function renderImages(images) {
    return images.map((image) => (
      <LazyImage key={image}  alt={image} />
    ));
  }

  return <div>{renderImages(images)}</div>;

}

export default LazyImageFolder;
