import React, { useState, useEffect } from "react";
import './LazyImageFolder.css'

function LazyImage({ src }) {
  const [isVisible, setIsVisible] = useState(false);
  console.log("hello")
  useEffect(() => {
    const handleScroll = () => {
      if (!isVisible) {
        const top = window.pageYOffset;
        const bottom = top + window.innerHeight;
        const el = document.getElementById("image");
        const rect = el.getBoundingClientRect();

        if (rect.top >= 0 && rect.bottom <= bottom) {
          setIsVisible(true);
        }
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [isVisible]);

  return (
    <div>
      {isVisible && (
        <img src={`data:image/jpeg;base64,${src}`} alt="Lazy-loaded image" />
      )}
    </div>
  );
};

function LazyImageFolder({ dir, onCloseLazyImage }) {
  const [images, setImages] = useState([]);

  useEffect(() => {
    fetch(`/api/images?dir=${encodeURIComponent(dir)}`)
      .then((response) => response.json())
      .then((data) => setImages(data.imageByteList))
      .catch((error) => console.error(error));
  }, [dir]);

  console.log("in load image")
  function renderImages(images) {
    return images.map((image_byte, index) => (
        <LazyImage key={index} src={image_byte} />
    ));
  }

  return (
    <div className="lazy-component">
      <button onClick={() => onCloseLazyImage()}>Go Back</button>
      <div>
        {renderImages(images)}
      </div>;
    </div>
  )

}

export default LazyImageFolder;
