import './App.css';
import React, { useEffect } from 'react';
import { useStateValue } from './StateProvider';

function App() {
  const [{ category }, dispatch] = useStateValue("");

  let presentLayer = 0;

  useEffect(() => { 
      switch(category) {
        case 'book':
          presentLayer = 1;
          return;
        default:
          presentLayer = 0;
      }  
  },[category]) 

  const handleCategorySelect = (category) => {
    dispatch({
        type: 'filter',
        item: {
          category: category,
        },
    });
  };

  if (presentLayer === 0) {
    return (
      <div className="app">

        {/* <div className="head">
          <h1>Hello Please select a category</h1>
        </div> */}

        <div className="category-menu">
              <div onClick={() => handleCategorySelect("book")}>Book</div>
              <div onClick={() => handleCategorySelect("category2")}>Category 2</div>
              <div onClick={() => handleCategorySelect("category3")}>Category 3</div>
        </div>
        <div>

        </div>

      </div>
    );
  } else if (presentLayer === 1) {
    return (
      <div className="app">

        {/* <div className="head">
          <h1>Hello Please select a category</h1>
        </div> */}

        <div className="category-menu">
              <div onClick={() => handleCategorySelect("book")}>全球中文网址速查手册（2000）</div>
              <div onClick={() => handleCategorySelect("category2")}>2002因特网黄页网址簿</div>
              <div onClick={() => handleCategorySelect("category3")}>全球中文网址速查手册（1999）</div>
              <div onClick={() => handleCategorySelect("category3")}>2001因特网黄页网址簿</div>
              <div onClick={() => handleCategorySelect("category3")}>Internet 全中文网址簿</div>
              <div onClick={() => handleCategorySelect("category3")}>Harley Hahn's Internet and Web Yellow Pages, Millennium Edition</div>
              <div onClick={() => handleCategorySelect("category3")}>Que's Official Internet Yellow Pages 2001 Edition</div>
        </div>
        <div>

        </div>

      </div>
    );
  }

};

export default App;
