import React, { useState, useEffect } from "react";
import LazyLoad from "react-lazyload";

function LazyImage({ image }) {
  return (
    <LazyLoad height={200}>
      <img src='./ads_img/610a44e321af549e5df057c4-20021129001537-2.gif'
 alt={image.substring(2)} />
    </LazyLoad>
  );
}

function LazyImageFolder({ dir }) {
  const [images, setImages] = useState([]);

  useEffect(() => {
    fetch(`/api/images?dir=${encodeURIComponent(dir)}`)
      .then((response) => response.json())
      .then((data) => setImages(data.imagePaths))
      .catch((error) => console.error(error));
  }, [dir]);

  console.log("in load image")
  function renderImages(images) {
    return images.map((image) => (
      <LazyImage image={image} />
    ));
  }

  return <div>{renderImages(images)}</div>;

}

export default LazyImageFolder;
