import './App.css';
import React from 'react';
import { useState } from 'react';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      selectedOption: "Option 1"
    };

  }

  handleOptionChange = (option) => {
    this.setState({ selectedOption: option });
  }

  render() {
    return (
      <div>
        <div>
          <Sidebar onOptionSelect={this.handleOptionChange.bind(this)}/>
        </div>
        <div class="main">
          <p>{this.state.selectedCategory}</p>
        </div>
      </div>


    );
  }
}

function Sidebar(props) {
  return (
    <div class="sidenav">
      <select>
        <option onClick={() => props.onCategorySelect("Option 1")}>Option 1</option>
        <option onClick={() => props.onCategorySelect("Option 2")}>Option 2</option>
        <option onClick={() => props.onCategorySelect("Option 3")}>Option 3</option>
      </select>
    </div>
  );
}


export default App;
