export const initialState = {
    tagList: [
      {'filter': 'book', 'data':[]},
      {'filter': 'year', 'data':[]},
      {'filter': 'category', 'data':[]},
    ],

    element: [],

    total_tag: [],

    frame: [false,''],
  };

  // Selector
  export const generate = (category) => 
   category?.reduce((amount, item) => item.price + amount, 0);

  const reducer = (state, action) => {
    console.log(action.type)
    switch(action.type) {
      case 'addTag':
        var newTagList = [...state.tagList];
        newTagList = newTagList.map((item) => {
          let container = {};
          if (item.filter === action.item.filter && !item.data.includes(action.item.tag)) {
            container['filter'] = item.filter;
            container['data'] = [...item.data, action.item.tag];
          } else {
            container = item;
          }
          return container;
        });

        console.log(newTagList);
      return{
        ...state,
        tagList: newTagList,
      }

      case 'deleteTag':
        newTagList = [...state.tagList];
        newTagList = newTagList.map((item) => {
          let container = {};
          if (item.filter === action.item.filter) {
            container['filter'] = item.filter;
            container['data'] = item.data.splice(action.item.index, 1);
          } else {
            container = item;
          }
          return container;
        });
        return{
          ...state,
          tagList: newTagList,
        }

      case 'addElement': 

        var book_data = new Set()
        var year_data = new Set()
        var category_data = new Set()

        action.item.forEach(element => {
          book_data.add(element.book_name)
          year_data.add(element.year_published)
          category_data.add(element.category)
        });

        var new_total_tag = [[...book_data], [...year_data],[...category_data]]

        return{
          ...state,
          element: action.item,
          total_tag: new_total_tag
        }

      case 'openurl':
        return{
          ...state,
          frame:[true, 'https://web.archive.org/web/2000id_/' + action.item]
        }

      case 'closeurl':
        return {
          ...state,
          frame: [false, '']
        }

      default:
        return state;
    }

  };

  export default reducer;
