import React from "react";
import { useState } from "react";

const SideBar = () => {
    const [selectedCategory, setSelectedCategory] = useState("");

    const handleCategorySelect = (category) => {
        setSelectedCategory(category);
    }

    return (
        <div className="category-menu">
            <div onClick={() => handleCategorySelect("category1")}>
                Category 1
                <div>
                    <ul className="sub-category-menu">
                        <li>Subcategory 1</li>
                        <li>Subcategory 2</li>
                        <li>Subcategory 3</li>
                    </ul>
                </div>
            </div>
            <div onClick={() => handleCategorySelect("category2")}>Category 2</div>
            <div onClick={() => handleCategorySelect("category3")}>Category 3</div>
        </div>
    )

}

export default SideBar;
