import React, { useEffect } from "react";
import { useStateValue } from "./StateProvider";

function SideBar() {
    const [{ category }] = useStateValue()

    return (
        <div>
            <p>
                ?{this.category}
            </p>
        </div>
    )

}

function Update() {
    const [{ category }] = useStateValue()

    useEffect(() => {  
        SideBar();
    },[category]) 
}

export default SideBar;
