export const initialState = {
    tagList: [
      {'filter': 'book', 'data':[]},
      {'filter': 'year', 'data':[]},
      {'filter': 'category', 'data':[]},
    ],

    element: [],
  };

  // Selector
  export const generate = (category) => 
   category?.reduce((amount, item) => item.price + amount, 0);

  const reducer = (state, action) => {
    console.log(action.item)
    switch(action.type) {
      case 'addTag':
        var newTagList = [...state.tagList];
        newTagList = newTagList.map((item) => {
          let container = {};
          if (item.filter === action.item.filter && !item.data.includes(action.item.tag)) {
            container['filter'] = item.filter;
            container['data'] = [...item.data, action.item.tag];
          } else {
            container = item;
          }
          return container;
        });

        console.log(newTagList);
      return{
        ...state,
        tagList: newTagList,
      }

      case 'deleteTag':
        newTagList = [...state.tagList];
        newTagList = newTagList.map((item) => {
          let container = {};
          if (item.filter === action.item.filter) {
            container['filter'] = item.filter;
            container['data'] = item.data.splice(action.item.index, 1);
          } else {
            container = item;
          }
          return container;
        });
        return{
          ...state,
          tagList: newTagList,
        }

      case 'addElement':
        console.log('before add element');
        console.log(action.item);
        var newElement = action.item.map((e) => {
          let container = {};
          container['book'] = e['book_name'];
          container['url'] = e['url'];
          container['year'] = e['year_published'];
          container['category'] = e['category'];
          return container;
        });
        return{
          ...state,
          element: newElement,
        }


      default:
        return state;
    }

  };

  export default reducer;
