import './TagList.css'
import React from 'react';


// example project data
const projects = [
  {
    title: 'Tigertail Virtual Museum',
    url: 'https://web.archive.org/web/19990508051230id_/http://www.tigtail.org/tvm/x1/baroque-spanish.html',
    tags: ['react', 'javascript', 'game', 'back-end']
  },
  {
    title: '綠界喜帖賀卡快印',
    url: 'https://web.archive.org/web/19970418065020id_/http://www.greenworld.com.tw/marry/index.htm',
    tags: ['react', 'javascript', 'game', 'web', 'front-end']
  },
  {
    title: '广州视窗——电子贺卡',
    url: 'https://web.archive.org/web/19980131103745id_/http://www.gznet.com/card/index1.htm',
    tags: ['javascript', 'utility', 'web']
  }
]

class Helpers {
  static contains(orig, filter) {
    let res = filter.map(item => {
      return orig.includes(item);
    });
    return !res.includes(false);
  }

  static hasDuplicates(array) {
    return (new Set(array)).size !== array.length;
  }
}

// components
class TagList extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      tags: ['game']
    }

    this.handleTagChange = this.handleTagChange.bind(this);
    this.handleListTagClick = this.handleListTagClick.bind(this);
  }

  handleTagChange(tags) {
    this.setState({tags});
  }

  handleListTagClick(tag) {
    this.setState({tags: [...this.state.tags, tag]});
  }

  handleUrlClick(url) {
    console.log(url)
    this.props.onSelectUrl(url);
  }

  render() {
    return (
      <div>
        <TagInput onTagChange={this.handleTagChange} tags={this.state.tags} />
        <ExampleList 
          items={projects} 
          filter={this.state.tags} 
          onTagClick={this.handleListTagClick} 
          onUrlClick={this.handleUrlClick}
        />
      </div>
    );
  }
}

class TagInput extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      inputValue: '',
      tags: this.props.tags || []
    }

    this.handleNewTag = this.handleNewTag.bind(this);
    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleKeyDown = this.handleKeyDown.bind(this);
    this.handleTagDelete = this.handleTagDelete.bind(this);
    this.deleteTag = this.deleteTag.bind(this);
    this.notDuplicate = this.notDuplicate.bind(this);
    this.updateControlledTags = this.updateControlledTags.bind(this);
  }

  handleNewTag(tags) {
    if (this.props.onNewTag) this.props.onNewTag(tags);
    if (this.props.onTagChange) this.props.onTagChange(tags);
  }

  handleInputChange({target: { value: inputValue }}) {
    inputValue = inputValue === ',' ? '' : inputValue;
    this.setState({inputValue});
  }

  handleKeyDown(e) {
    let { key, target: {value} } = e;
    let { tags } = this.state;
    switch (key) {
      case 'Tab':
        if (value) e.preventDefault();
      case 'Enter':
      case ',':
        value = value.trim();
        if (value && this.notDuplicate(tags, value)) {
          this.addTag(value);
        } else {
          this.setState({inputValue: ''})
        }
        break;
      case 'Backspace':
        if (!value) {
          this.handleTagDelete(tags.length - 1);
        }
        break;
    }
  }

  handleTagDelete(index, e) {
    this.deleteTag(index, () => {
      this.props.onTagChange(this.state.tags);
    });
  }

  deleteTag(index, callback) {
    let tags = this.state.tags.slice();

    tags.splice(index, 1);
    this.setState({ tags }, () => {
      if (callback) callback();
    });
  }

  notDuplicate(tags, newTag) {
    return (!tags.includes(newTag) || this.props.allowDuplicates);
  }

  addTag(tag) {
    if (this.notDuplicate(this.state.tags, tag)) {
      this.setState({tags: [...this.state.tags, tag], inputValue: ''}, () => {
        this.handleNewTag(this.state.tags);
      });
    }
  }

  updateControlledTags(tags) {
    if (tags && !Helpers.hasDuplicates(tags)) {
      this.setState({ tags }, () => {
        // this.props.onTagChange(tags);
      });
    }
  }

  componentWillReceiveProps(nextProps) {
    this.updateControlledTags(nextProps.tags);
  }

  render() {
    return (
      <span className="tagInputWrapper">
        <TagsList 
          tags={this.state.tags} 
          onTagDelete={this.handleTagDelete} 
          hashtag={this.props.hashtag}
        />
        <input 
          name="tagInput" 
          className="tagInput" 
          placeholder="enter a tag..." 
          value={this.state.inputValue} 
          onChange={this.handleInputChange}
          onKeyDown={this.handleKeyDown}
        />
      </span>
    );
  }
}

const TagsList = ({tags, onTagDelete, hashtag}) => {
  let list = tags.map((tag, index) => (
    <Tag 
      name={tag} 
      onDelete={onTagDelete} 
      index={index} 
      hashtag={hashtag} />
  ));
  return (
    <ul name="tagsList" className="tagsList">
      {list}
    </ul>
  )
}

const Tag = ({name, index, onDelete, hashtag}) => {
  return (
    <li>
      {hashtag && (
        <span style={{color: 'rgb(87, 7, 7)', fontWeight: 'bold'}}># </span>
      )}
      {name} 
      <a href="#" onClick={e => onDelete(index, e)}>x</a>
    </li>
  );
}

const ExampleList = ({items, filter, onTagClick, onUrlClick}) => {
  let filtered = items.filter(item => Helpers.contains(item.tags, filter));

  let renderedItems = filtered.map(({title, description, url, tags}, index) => {
    return (
      <ExampleListItem 
        title={title} 
        description={description}
        url={url}
        tags={tags}
        key={index}
        onClick={onTagClick}
        onClickUrl={onUrlClick}
      />
    );
  });

  return (
    <div>
      <ul 
        style={{
          display: 'grid',
          gridTemplateColumns: '1fr 1fr 1fr',
          padding: '0px',
          gridGap: '10px'

      }}>
        {renderedItems}
      </ul>
    </div>
  );
}

const ExampleListItem = ({title, description, tags, url, key, onClick, onClickUrl}) => {
  const renderedTags = tags.map((tag, index) => (
    <li onClick={(e) => onClick(tag, e)} className="tag clickable">
      {tag}
    </li>
  ));

  return (
    <li style={{padding: 10, listStyle: 'none', background: '#efefef', borderRadius: '10px', boxShadow: '1px 1px 5px #aaa'}}>
      <h2 onClick={(e) => onClickUrl(url, e)} style={{color: 'black'}} >{title}</h2>
      <p>{description}</p>
      <ul className='tagsList' style={{padding: 0}}>{renderedTags}</ul>
    </li>
  );
}

export default TagList
