import './App.css';
import React from 'react';
import { useStateValue } from './StateProvider';
import SideBar from './SideBar';

function App() {
  const [state, dispatch] = useStateValue("");

  const handleCategorySelect = (category) => {
      dispatch({
          type: 'category_select',
          item: {
            category: category,
          },
      });
  };

  return (
    <div className="app">
      <h1>Hello Please select a category</h1>
      <div className="category-menu">
            <div onClick={() => handleCategorySelect("category1")}>Category 1</div>
            <div onClick={() => handleCategorySelect("category2")}>Category 2</div>
            <div onClick={() => handleCategorySelect("category3")}>Category 3</div>
      </div>

    </div>
  );
};

export default App;
