import './ListItem.css';
import { useStateValue } from "./StateProvider";


function ListItem({ url, book_name, year_published, category, onClickUrl }) {
    const [state, dispatch] = useStateValue();

    const tags = book_name.concat(year_published, category);

    const onTagClick = (tag) => {
        console.log("I did it");
    }

    const renderedTags = tags.map((tag, index) => (
        <li onClick={(e) => onTagClick(tag, e)} className="tag clickable">
          {tag}
        </li>
    ));

    return (
    <li style={{padding: 10, listStyle: 'none', background: '#efefef', borderRadius: '10px', boxShadow: '1px 1px 5px #aaa'}}>
        <h2 onClick={(e) => onClickUrl(url, e)} style={{color: 'black'}} >{url}</h2>
        <ul className='tagsList' style={{padding: 0}}>{renderedTags}</ul>
    </li>
    );
}

export default ListItem;
