import './App.css';
import React from 'react';
import { useState } from 'react';

const [selectedCategory, setSelectedCategory] = useState(null);

const handleCategorySelect = (category) => {
  setSelectedCategory(category);
}

function App() {
    return (
      <div className="category-menu">
        <div onClick={() => handleCategorySelect("category1")}>Category 1</div>
        <div onClick={() => handleCategorySelect("category2")}>Category 2</div>
        <div onClick={() => handleCategorySelect("category3")}>Category 3</div>
      </div>
    );
};

export default App;
